FinancialPlanner.Controllers = (function(config, uiService, errorService) {
  return {
    createIncomeVsExpensesChart: wrapWithFeedback(
      function() {
        return FinancialPlanner.VisualizationService.createIncomeVsExpensesChart();
      },
